    const _past = [...past, present];
    const maxLengthNum = isNumber(maxLength) ? maxLength : Number(maxLength);
    // maximum number of records exceeded
    if (maxLengthNum > 0 && _past.length > maxLengthNum) {
      //delete first
      _past.splice(0, 1);
    }
    setHistory({
      present: val,
      future: [],
      past: _past
    });
  };
  const _forward = (step = 1) => {
    if (future.length === 0) {
      return;
    }
    const {
      _before,
      _current,
      _after
    } = split(step, future);
    setHistory({
      past: [...past, present, ..._before],
      present: _current,
      future: _after
    });
  };
  const _backward = (step = -1) => {
    if (past.length === 0) {
      return;
    }
    const {
      _before,
      _current,
      _after
    } = split(step, past);
    setHistory({
      past: _before,
      present: _current,
      future: [..._after, present, ...future]
    });
  };
