import React from 'react';
import { useDispatch, useSelector } from 'react-redux';
import axios from 'axios';
import qs from 'qs';

import Categories from '../components/Categories';
import Sort from '../components/Sort';
import PizzaBlock from '../components/PizzaBlock';
import Pagination from '../components/Pagination';
import { SearchContext } from '../App';
import { setCategoryId, setPageCount } from '../redux/slice/filterSlice';

const Home = () => {
	const dispatch = useDispatch();
	const { categoryId, sort, pageCount } = useSelector((state) => state.filterSlice);
	const { searchValue } = React.useContext(SearchContext);
	const [items, setItems] = React.useState([]);
	// const [currentPage, setCurrentPage] = React.useState(1);

	const onClickCategory = (id) => {
		dispatch(setCategoryId(id));
	};
	const onChangePage = (number) => {
		dispatch(setPageCount(number));
	};

	React.useEffect(() => {
		const sortBy = sort.sortPropety.replace('-', '');
		const order = sort.sortPropety.includes('-') ? 'asc' : 'desc';
		const category = categoryId > 0 ? `&category_like=${categoryId}` : '';
		const search = searchValue ? `&title_like=${searchValue}` : '';

		axios
			.get(
				`http://localhost:3001/pizzas?_page=${pageCount}&_limit=4${category}&_sort=${sortBy}&_order=${order}${search}`,
			)
			.then((res) => {
				setItems(res.data);
			});

			

		window.scrollTo(0, 0);
	}, [categoryId, sort.sortPropety, searchValue, pageCount]);
	return (
		<div className="container">
			<div className="content__top">
				<Categories value={categoryId} onClickCategory={onClickCategory} />
				<Sort />
			</div>
			<h2 className="content__title">Все пиццы</h2>
			<div className="content__items">
				{items.map((obj) => (
					<PizzaBlock key={obj.id} {...obj} />
				))}
			</div>
			<Pagination pageCount={pageCount} onChangePage={onChangePage} />
		</div>
	);
};

export default Home;

// https://63e004e28b24964ae0f85b6a.mockapi.io/pizza

