import React from 'react';
import { useDispatch, useSelector } from 'react-redux';

import Categories from '../components/Categories';
import Sort from '../components/Sort';
import PizzaBlock from '../components/PizzaBlock';

import Pagination from '../components/Pagination';
import { SearchContext } from '../App';
import { setCategoryId, setPageCount } from '../redux/slice/filterSlice';
import { fetchPizzas } from '../redux/slice/pizzaSlice';
import Skeleton from '../components/Skeleton';

const Home = () => {
	const dispatch = useDispatch();

	const { items, status } = useSelector((state) => state.pizzaSlice);
	const { categoryId, sort, pageCount } = useSelector((state) => state.filterSlice);
	const { searchValue } = React.useContext(SearchContext);
	// const [items, setItems] = React.useState([]);

	const onClickCategory = (id) => {
		dispatch(setCategoryId(id));
	};
	const onChangePage = (page) => {
		dispatch(setPageCount(page));
	};
	const getPizzas = async () => {
		const sortBy = sort.sortPropety.replace('-', '');
		const order = sort.sortPropety.includes('-') ? 'asc' : 'desc';
		const category = categoryId > 0 ? `&category_like=${categoryId}` : '';
		const search = searchValue ? `&title_like=${searchValue}` : '';

		dispatch(
			fetchPizzas({
				sortBy,
				order,
				category,
				search,
				pageCount,
			}),
		);
	};

	React.useEffect(() => {
		getPizzas();
	}, [categoryId, sort.sortPropety, searchValue, pageCount]);

	// React.useEffect(() => {
	// 	if (window.location.search) {
	// 		const params = qs.parse(window.location.search.substring(1));
	// 		const sort = sortList.find((obj) => obj.sortPropety === params.sortPropety);
	// 		dispatch(
	// 			setFilters({
	// 				...params,
	// 				sort,
	// 			}),
	// 		);
	// 		isSearch.current = true;
	// 	}
	// }, []);

	// React.useEffect(() => {
	// 	window.scrollTo(0, 0);
	// 	if(isSearch.current){
	// 		fetchPizzas()
	// 	}
	// 	isSearch.current = false;

	// }, [categoryId, sort.sortPropety, searchValue, pageCount]);

	// React.useEffect(() => {
	// if(isMounted.current){
	// 	const queryString = qs.stringify({
	// 		sortPropety: sort.sortPropety,
	// 		categoryId,
	// 		pageCount,
	// 	});
	// 	navigate(`?${queryString}`);
	// }
	// isMounted.current = true
	// }, [categoryId, sort.sortPropety, pageCount]);
	const pizzas = items.map((obj) => <PizzaBlock key={obj.id} {...obj} />);
	const skeletons = [...new Array(6)].map((_, index) => <Skeleton key={index} />);
	return (
		<div className="container">
			<div className="content__top">
				<Categories value={categoryId} onClickCategory={onClickCategory} />
				<Sort />
			</div>
			<h2 className="content__title">–í—Å–µ –ø–∏—Ü—Ü—ã</h2>
			{
				status === 'error' ? <div>
					<h2>
					–ö–æ—Ä–∑–∏–Ω–∞ –ø—É—Å—Ç–∞—è <icon>üòï</icon>
				</h2>
				<p>
					–í–µ—Ä–æ—è—Ç–Ω–µ–π –≤—Å–µ–≥–æ, –≤—ã –Ω–µ –∑–∞–∫–∞–∑—ã–≤–∞–ª–∏ –µ—â—ë –ø–∏—Ü—Ü—É.
					<br />
					–î–ª—è —Ç–æ–≥–æ, —á—Ç–æ–±—ã –∑–∞–∫–∞–∑–∞—Ç—å –ø–∏—Ü—Ü—É, –ø–µ—Ä–µ–π–¥–∏ –Ω–∞ –≥–ª–∞–≤–Ω—É—é —Å—Ç—Ä–∞–Ω–∏—Ü—É.
				</p>
				</div> :
			<div className="content__items">{status === 'loading' ? skeletons : pizzas}</div>
			}
			<Pagination pageCount={pageCount} onChangePage={onChangePage} />
		</div> 
	);
};

export default Home;

