import React from 'react';
const Categories = () => {
	const categories = ['Ы','К','Е','Н','Г','Н']
	const [actTake, setActTake] = React.useState(0)
	return (
		<div className="categories">
			<ul>
				
				{categories.map((item, i)=>(
					
					<li onClick= {function(){setActTake(i)}} className={actTake ===  i ? "active":""}>{item}</li>
				))}
			</ul>
		</div>
	);
};

export default Categories;
