import React from 'react';

import Categories from '../components/Categories';
import Sort from '../components/Sort';
import PizzaBlock from '../components/PizzaBlock';
import Pagination from '../components/Pagination';
import { SearchContext } from '../App';

const Home = () => {
  const {searchValue, setSearchValue} = React.useContext(SearchContext);
	const [items, setItems] = React.useState([]);
	const [categoryId, setCategoryId] = React.useState(0);
	const [currentPage, setCurrentPage] = React.useState(1);
	const [sortType, setSortType] = React.useState({
		name: 'популярности',
		sortPropety: 'rating',
	});


	React.useEffect(() => {
		const sortBy = sortType.sortPropety.replace('-', '');
		const order = sortType.sortPropety.includes('-') ? 'asc' : 'desc';
		const category = categoryId > 0 ? `category=${categoryId}` : '';
		const search = searchValue  ? `&search=${searchValue}` : '';

		fetch(
			`https://63e004e28b24964ae0f85b6a.mockapi.io/pizza?page=${currentPage}&limit=4${category}&sortBy=${sortBy}&order=${order}${search}`,
		)
			.then((res) => res.json())
			.then((arr) => {
				setItems(arr);
			});
			window.scrollTo(0, 0);
	}, [categoryId,sortType,searchValue,currentPage]);
	return (
		<div className="container">
			<div className="content__top">
				<Categories value={categoryId} onClickCategory={(i) => setCategoryId(i)} />
				<Sort value={sortType} onClickSort={(i) => setSortType(i)} />
			</div>
			<h2 className="content__title">Все пиццы</h2>
			<div className="content__items">
				{items.map((obj) => (
					<PizzaBlock key={obj.id} {...obj} />
				))}
			</div>
			<Pagination onChangePage={(number)=>setCurrentPage(number)}/>
		</div>
	);
};

export default Home;
