    setValue(c => c + delta);
  };
  const dec = (delta = 1) => {
    setValue(c => c - delta);
  };
  const set = value => {
    setValue(value);
  };
  const reset = () => {
    setValue(initialValue);
  };
  return [current, {
    inc: useMemoizedFn(inc),
    dec: useMemoizedFn(dec),
    set: useMemoizedFn(set),
    reset: useMemoizedFn(reset)
  }];
